import React, { Component } from 'react';

import { appSocket } from './appSocket';

interface MensagemPayload {
  mensagem: string;
}

interface State {
  mensagens: MensagemPayload[];
}

export class ListarMensagens extends Component<{}, State> {
  state: State = {
    mensagens: [{ mensagem: "Teste" }]
  };

  componentDidMount() {

    appSocket.on("status", (mensagens: MensagemPayload[]) => {
      this.setState({
        mensagens
      });
    });
  }

  renderMensagens = () => {
    return this.state.mensagens.map(mensagem => <Mensagem {...mensagem} />);
  };

  render() {
    return <ul className="ListarMensagens">{this.renderMensagens()}</ul>;
  }
}

const Mensagem = ({ mensagem }: MensagemPayload) => {
  return (
    <li className="Mensagem">
      <p>
        {mensagem} -
        <small>
          <i> {mensagem}</i>
        </small>
      </p>
    </li>
  );
};
