import React, { Component } from 'react';

import { appSocket } from './appSocket';

interface MensagemPayload {
  nome: string;
  mensagem: string;
  idx: any;
}

interface State {
  mensagens: MensagemPayload[];
}

export class ListarMensagens extends Component<{}, State> {
  state: State = {
    mensagens: [{ nome: "m4r6i0", mensagem: "Teste", idx: 1 }]
  };

  componentDidMount() {

    appSocket.on("chat msg", (mensagens: MensagemPayload[]) => {
      this.setState({
        mensagens
      });
    });
  }

  renderMensagens = () => {
    return this.state.mensagens.map((mensagem, idx) => <Mensagem {...mensagem} />);
  };

  render() {
    return <ul className="ListarMensagens">{this.renderMensagens()}</ul>;
  }
}

const Mensagem = ({ mensagem, nome}: MensagemPayload) => {
  return (
    <li className="Mensagem" key={mensagem}>
      <p>
        {mensagem} - {idx}
        <small>
          <i> {nome}</i>
        </small>
      </p>
    </li>
  );
};
