import React, { Component } from 'react';

import { appSocket } from './appSocket';

interface MensagemPayload {
  nome: string;
  mensagem: string;
}

interface State {
  mensagens: MensagemPayload[];
}

export class ListarMensagens extends Component<{}, State> {
  state: State = {
    mensagens: [{ nome: "m4r6i0", mensagem: "Teste"}]
  };

  componentDidMount() {

    appSocket.on("chat msg", (mensagens: MensagemPayload[]) => {
      this.setState({
        mensagens
      });
    });
  }

  // renderMensagens = () => {
  //   return this.state.mensagens.map((mensagem) => <Mensagem {...mensagem} />);
  // };

  renderMensagens = () => {
    console.log(this.state.mensagens);
    return this.state.mensagens.map(item =>{
      <li key={item.mensagem}>
      <p>
        {item.mensagem}
        <small>
          <i> {item.nome}</i>
        </small>
      </p>
    </li>
    });
  }

  render() {
    return <ul className="ListarMensagens">{this.renderMensagens()}</ul>;
  }
}

const Mensagem = ({ mensagem, nome}: MensagemPayload) => {
  return (

    <li className="Mensagem" key={Mensagem.toString()}>
      <p>
        {mensagem}
        <small>
          <i> {nome}</i>
        </small>
      </p>
    </li>
  );
};
